package week1.day2;

public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//print even numbers
		int num=10;
		for (int i = 0; i<=num; i++) {//i=1;i<=10
			if(i%2==0) {//1%2==0->f//4%2==2->t
				System.out.println(i);
			}
			
			
		}

	}

}
